import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { removeFromCart, updateCartItemQuantity, clearCart } from '../features/cart/cartSlice';
import { setAlert } from '../features/alerts/alertSlice';
import Spinner from '../components/layout/Spinner';
import styled from 'styled-components';

const CartContainer = styled.div`
  padding: 2rem 0;
`;

const CartTitle = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 2rem;
  color: var(--text-color);
`;

const CartContent = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const CartItems = styled.div`
  flex: 1;
  min-width: 0; /* For proper flexbox behavior */
`;

const CartSummary = styled.div`
  flex: 0 0 350px;

  @media (max-width: 768px) {
    flex: 1 0 100%;
  }
`;

const SummaryCard = styled.div`
  background-color: white;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  padding: 1.5rem;
  position: sticky;
  top: 2rem;
`;

const SummaryTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  color: var(--text-color);
`;

const SummaryRow = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 1rem;
  font-size: ${props => props.total ? '1.2rem' : '1rem'};
  font-weight: ${props => props.total ? '600' : '400'};
  color: ${props => props.total ? 'var(--primary-color)' : 'var(--text-color)'};
  padding-top: ${props => props.total ? '1rem' : '0'};
  border-top: ${props => props.total ? '1px solid var(--border-color)' : 'none'};
`;

const CheckoutButton = styled.button`
  background-color: var(--primary-color);
  color: white;
  border: none;
  border-radius: 4px;
  padding: 0.8rem;
  font-size: 1.1rem;
  font-weight: 500;
  width: 100%;
  cursor: pointer;
  transition: background-color 0.3s ease;
  margin-top: 1.5rem;

  &:hover {
    background-color: var(--primary-dark);
  }

  &:disabled {
    background-color: #ccc;
    cursor: not-allowed;
  }
`;

const ClearCartButton = styled.button`
  background-color: transparent;
  color: var(--light-text);
  border: none;
  font-size: 0.9rem;
  cursor: pointer;
  margin-top: 1rem;
  padding: 0;
  text-align: center;
  width: 100%;

  &:hover {
    color: var(--error-color);
    text-decoration: underline;
  }
`;

const CartItemCard = styled.div`
  display: flex;
  background-color: white;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  padding: 1.5rem;
  margin-bottom: 1.5rem;

  @media (max-width: 576px) {
    flex-direction: column;
  }
`;

const ItemImage = styled.img`
  width: 100px;
  height: 100px;
  object-fit: cover;
  border-radius: 4px;
  margin-right: 1.5rem;

  @media (max-width: 576px) {
    width: 100%;
    height: 150px;
    margin-right: 0;
    margin-bottom: 1rem;
  }
`;

const ItemDetails = styled.div`
  flex: 1;
  min-width: 0; /* For text truncation */
`;

const ItemTitle = styled(Link)`
  font-size: 1.2rem;
  font-weight: 500;
  margin: 0 0 0.5rem 0;
  color: var(--text-color);
  text-decoration: none;
  display: block;

  &:hover {
    color: var(--primary-color);
  }
`;

const ItemMeta = styled.div`
