const EmptyCartTitle = styled.h2`
  color: var(--text-color);
  margin-bottom: 1rem;
`;

const EmptyCartText = styled.p`
  color: var(--light-text);
  margin-bottom: 1.5rem;
`;

const ShopButton = styled(Link)`
  display: inline-block;
  background-color: var(--primary-color);
  color: white;
  padding: 0.8rem 1.5rem;
  border-radius: 4px;
  text-decoration: none;
  font-weight: 500;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: var(--primary-dark);
  }
`;

const Cart = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { cartItems, loading } = useSelector(state => state.cart);

  const handleQuantityChange = (item, newQuantity) => {
    const quantity = parseInt(newQuantity);
    if (!isNaN(quantity) && quantity > 0 && quantity <= 100) {
      dispatch(updateCartItemQuantity({
        id: item._id, 
        size: item.size, 
        color: item.color, 
        quantity
      }));
    }
  };

  const handleRemoveItem = (item) => {
    dispatch(removeFromCart(item));
    dispatch(setAlert({
      msg: 'Item removed from cart',
      type: 'success'
    }));
  };

  const handleClearCart = () => {
    dispatch(clearCart());
    dispatch(setAlert({
      msg: 'Cart cleared',
      type: 'success'
    }));
  };

  const handleCheckout = () => {
    navigate('/checkout');
  };

  // Calculate order summary
  const subtotal = cartItems.reduce((acc, item) => acc + (item.price * item.quantity), 0);
  const shipping = subtotal > 0 ? 5.99 : 0;
  const tax = subtotal * 0.08; // 8% tax
  const total = subtotal + shipping + tax;

  if (loading) {
    return <Spinner />;
  }

  if (cartItems.length === 0) {
    return (
      <CartContainer>
        <CartTitle>Your Cart</CartTitle>
        <EmptyCart>
          <EmptyCartTitle>Your cart is empty</EmptyCartTitle>
          <EmptyCartText>
            Looks like you haven't added any items to your cart yet.
          </EmptyCartText>
          <ShopButton to="/merchandise">Shop Now</ShopButton>
        </EmptyCart>
      </CartContainer>
    );
  }

  return (
    <CartContainer>
      <CartTitle>Your Cart ({cartItems.length} {cartItems.length === 1 ? 'item' : 'items'})</CartTitle>

      <CartContent>
        <CartItems>
          {cartItems.map((item, index) => (
            <CartItemCard key={`${item._id}-${item.size}-${item.color}-${index}`}>
              <ItemImage src={item.imageUrl} alt={item.name} />
              <ItemDetails>
                <ItemTitle to={`/merchandise/${item._id}`}>{item.name}</ItemTitle>
                <ItemMeta>
                  <ItemAttribute>{item.category}</ItemAttribute>
                  {item.size !== 'N/A' && <ItemAttribute>Size: {item.size}</ItemAttribute>}
                  {item.color && (
                    <ItemAttribute>
                      <ItemColorDot color={item.color} />
                      Color
                    </ItemAttribute>
                  )}
                </ItemMeta>
                <ItemPrice>${(item.price * item.quantity).toFixed(2)}</ItemPrice>
                <ItemActions>
                  <QuantityControl>
                    <QuantityButton
                      onClick={() => handleQuantityChange(item, item.quantity - 1)}
                      disabled={item.quantity <= 1}
                    >
                      -
                    </QuantityButton>
                    <QuantityInput
                      type="number"
                      min="1"
                      max="100"
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(item, e.target.value)}
                    />
                    <QuantityButton
                      onClick={() => handleQuantityChange(item, item.quantity + 1)}
                      disabled={item.quantity >= 100}
                    >
                      +
                    </QuantityButton>
                  </QuantityControl>
                  <RemoveButton onClick={() => handleRemoveItem(item)}>
                    Remove
                  </RemoveButton>
                </ItemActions>
              </ItemDetails>
            </CartItemCard>
          ))}
        </CartItems>

        <CartSummary>
          <SummaryCard>
            <SummaryTitle>Order Summary</SummaryTitle>
            <SummaryRow>
              <span>Subtotal</span>
              <span>${subtotal.toFixed(2)}</span>
            </SummaryRow>
            <SummaryRow>
              <span>Shipping</span>
              <span>${shipping.toFixed(2)}</span>
            </SummaryRow>
            <SummaryRow>
              <span>Tax (8%)</span>
              <span>${tax.toFixed(2)}</span>
            </SummaryRow>
            <SummaryRow total>
              <span>Total</span>
              <span>${total.toFixed(2)}</span>
            </SummaryRow>

            <CheckoutButton onClick={handleCheckout}>
              Proceed to Checkout
            </CheckoutButton>
            <ClearCartButton onClick={handleClearCart}>
              Clear Cart
            </ClearCartButton>
          </SummaryCard>
        </CartSummary>
      </CartContent>
    </CartContainer>
  );
};

export default Cart;
