const ProductDetail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { product, loading } = useSelector(state => state.merchandise);
  const { isAuthenticated } = useSelector(state => state.auth);

  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    dispatch(getMerchandiseById(id));
  }, [dispatch, id]);

  useEffect(() => {
    if (product) {
      // Set default selections if available
      if (product.availableSizes && product.availableSizes.length > 0) {
        setSelectedSize(product.availableSizes[0]);
      }

      if (product.availableColors && product.availableColors.length > 0) {
        setSelectedColor(product.availableColors[0]);
      }
    }
  }, [product]);

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0 && value <= (product?.stock || 1)) {
      setQuantity(value);
    }
  };

  const incrementQuantity = () => {
    if (quantity < (product?.stock || 1)) {
      setQuantity(quantity + 1);
    }
  };

  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const handleAddToCart = () => {
    if (!isAuthenticated) {
      dispatch(setAlert({
        msg: 'Please log in to add items to your cart',
        type: 'info'
      }));
      navigate('/login');
      return;
    }

    const cartItem = {
      _id: product._id,
      name: product.name,
      imageUrl: product.imageUrl,
      price: product.price,
      category: product.category,
      size: selectedSize || 'N/A',
      color: selectedColor || '',
      quantity,
      merchandiseId: product._id,
    };

    dispatch(addToCart(cartItem));

    dispatch(setAlert({
      msg: 'Item added to cart',
      type: 'success'
    }));
  };

  if (loading) {
    return <Spinner />;
  }

  if (!product) {
    return <div>Product not found</div>;
  }

  const inStock = product.stock > 0;

  return (
    <ProductContainer>
      <BreadcrumbNav>
        <BreadcrumbLink to="/">Home</BreadcrumbLink> / {' '}
        <BreadcrumbLink to="/merchandise">Merchandise</BreadcrumbLink> / {' '}
        {product.name}
      </BreadcrumbNav>

      <ProductContent>
        <ProductImageContainer>
          <ProductImage src={product.imageUrl} alt={product.name} />
        </ProductImageContainer>

        <ProductDetails>
          <ProductTitle>{product.name}</ProductTitle>
          <ProductPrice>${product.price.toFixed(2)}</ProductPrice>
          <ProductDescription>{product.description}</ProductDescription>

          <ProductMeta>
            <MetaItem>
              <MetaLabel>Category:</MetaLabel>
              <CategoryBadge>{product.category}</CategoryBadge>
            </MetaItem>

            {product.character && (
              <MetaItem>
                <MetaLabel>Character:</MetaLabel>
                <CharacterBadge to={`/characters/${product.character._id}`}>
                  <CharacterImage src={product.character.imageUrl} alt={product.character.name} />
                  {product.character.name}
                </CharacterBadge>
              </MetaItem>
            )}

            {product.creator && (
              <MetaItem>
                <MetaLabel>Creator:</MetaLabel>
                <CreatorBadge to={`/users/${product.creator._id}`}>
                  {product.creator.profilePicture && (
                    <CreatorImage src={product.creator.profilePicture} alt={product.creator.username} />
                  )}
                  {product.creator.username}
                </CreatorBadge>
              </MetaItem>
            )}

            <MetaItem>
              <MetaLabel>Availability:</MetaLabel>
              {inStock ? (
                <StockStatus inStock={true}>In Stock ({product.stock} available)</StockStatus>
              ) : (
                <StockStatus inStock={false}>Out of Stock</StockStatus>
              )}
            </MetaItem>
          </ProductMeta>

          <ProductOptions>
            {product.availableSizes && product.availableSizes.length > 0 && product.availableSizes[0] !== 'N/A' && (
              <OptionGroup>
                <OptionLabel>Size:</OptionLabel>
                <SizeOptions>
                  {product.availableSizes.map(size => (
                    <SizeButton
                      key={size}
                      selected={selectedSize === size}
                      onClick={() => setSelectedSize(size)}
                    >
                      {size}
                    </SizeButton>
                  ))}
                </SizeOptions>
              </OptionGroup>
            )}

            {product.availableColors && product.availableColors.length > 0 && (
              <OptionGroup>
                <OptionLabel>Color:</OptionLabel>
                <ColorOptions>
                  {product.availableColors.map(color => (
                    <ColorButton
                      key={color}
                      color={color}
                      selected={selectedColor === color}
                      onClick={() => setSelectedColor(color)}
                    />
                  ))}
                </ColorOptions>
              </OptionGroup>
            )}

            <OptionGroup>
              <OptionLabel>Quantity:</OptionLabel>
              <QuantityControl>
                <QuantityButton 
                  onClick={decrementQuantity}
                  disabled={quantity <= 1}
                >
                  -
                </QuantityButton>
                <QuantityInput
                  type="number"
                  min="1"
                  max={product.stock}
                  value={quantity}
                  onChange={handleQuantityChange}
                />
                <QuantityButton 
                  onClick={incrementQuantity}
                  disabled={quantity >= product.stock}
                >
                  +
                </QuantityButton>
              </QuantityControl>
            </OptionGroup>
          </ProductOptions>

          <AddToCartButton 
            onClick={handleAddToCart}
            disabled={!inStock}
          >
            {inStock ? 'Add to Cart' : 'Out of Stock'}
          </AddToCartButton>
        </ProductDetails>
      </ProductContent>
    </ProductContainer>
  );
};

export default ProductDetail;
